import React, { Component } from 'react';
import Home from './home'
import AddCrads from './addCards'
class App extends Component {
  constructor(props){
    super(props)
    this.state={
      open: false
    }
  }

  openAddPage=()=>{
    this.setState({open: true})
  }
  render() {
    const {open} = this.state
    return (
      <div className="App">
        {open===true? 
          <AddCrads/>:
          <Home openAddPage={this.openAddPage}/> 
        }
      </div>
    );
  }
}

export default App;
