import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        maxWidth:  "600px",
        fontFamily: "'Rakkas',cursive",
      },
      margin: {
        margin: theme.spacing.unit,
      },
      textField: {
        flexBasis: 200,
      },
  });

class AddCrads extends Component {
    state = {
        question: '',
        description: '',
        videoLink: '',
        articleLink: '',
      };

      handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
      }

  render() {
    const { classes } = this.props;
    return (
        <div className={classes.root}>
            <FormControl fullWidth className={classes.margin}>
                <InputLabel htmlFor="adornment-amount">Question</InputLabel>
                <Input
                    id="adornment-amount"
                    value={this.state.question}
                    onChange={this.handleChange('Description')}

                />
            </FormControl>
            <FormControl fullWidth className={classes.margin}>
                <InputLabel htmlFor="adornment-amount">Description</InputLabel>
                <Input
                    id="adornment-amount"
                    value={this.state.description}
                    onChange={this.handleChange('Description')}

                />
            </FormControl>
            <FormControl fullWidth className={classes.margin}>
                <InputLabel htmlFor="adornment-amount">Video Link</InputLabel>
                <Input
                    id="adornment-amount"
                    value={this.state.videoLink}
                    onChange={this.handleChange('Description')}

                />
            </FormControl>
            <FormControl fullWidth className={classes.margin}>
                <InputLabel htmlFor="adornment-amount">Artical Link</InputLabel>
                <Input
                    id="adornment-amount"
                    value={this.state.articleLink}
                    onChange={this.handleChange('Description')}

                />
            </FormControl>

      </div>
    );
  }
}

AddCrads.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(AddCrads);
