import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Button from '@material-ui/core/Button';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        maxWidth:  "600px",
        marginLeft: theme.spacing.unit*74,

      },
      margin: {
        margin: theme.spacing.unit*2,

      },
      textField: {
        flexBasis: 200,
      },
      chatagiri:{
        fontFamily: "'Chela One', cursive",
        fontSize: "34px"
      },
      blankInp:{
          heigth:"43px",
          fontFamily: "'Chela One', cursive",
          color: "#821515"
      }
  });

class AddCrads extends Component {
    state = {
        question: '',
        description: '',
        videoLink: '',
        articleLink: '',
      };

      handleChange = prop => event => {
        this.setState({ [prop]: event.target.value });
      }

      postPeriodData=()=>{
        const formData = new FormData();
        formData.append('question', this.state.question);
        formData.append('missingChildName', this.state.missingChildName);
        formData.append('Description', this.state.Description);
      }

  render() {
    const { classes } = this.props;
    return (
        <div>
            <div className={classes.root}>
                <FormControl fullWidth className={classes.margin}>
                    <InputLabel htmlFor="ques" className={classes.chatagiri}>Question</InputLabel>
                    <Input
                        id="ques"
                        className={classes.blankInp}
                        value={this.state.question}
                        onChange={this.handleChange('question')}

                    />
                </FormControl>
                <FormControl fullWidth className={classes.margin}>
                    <InputLabel htmlFor="des" className={classes.chatagiri}>Description</InputLabel>
                    <Input
                        id="des"
                        className={classes.blankInp}
                        value={this.state.description}
                        onChange={this.handleChange('description')}

                    />
                </FormControl>
                <FormControl fullWidth className={classes.margin}>
                    <InputLabel htmlFor="video" className={classes.chatagiri}>Video Link</InputLabel>
                    <Input
                        id="video"
                        className={classes.blankInp}
                        value={this.state.videoLink}
                        onChange={this.handleChange('videoLink')}

                    />
                </FormControl>
                <FormControl fullWidth className={classes.margin}>
                    <InputLabel htmlFor="article" className={classes.chatagiri}>Artical Link</InputLabel>
                    <Input
                        id="article"
                        className={classes.blankInp}
                        value={this.state.articleLink}
                        onChange={this.handleChange('articleLink')}

                    />
                </FormControl> 
                <Button variant="contained" color="secondary" onClick={this.postPeriodData}>
                    Post
                </Button>     
        </div>
      </div>
    );
  }
}

AddCrads.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(AddCrads);
